import React from "react";
import styled from "styled-components/native";

const ErrorStyled = styled.Text`
color: #bf1650;
width: 80%;
align-items: flex-end;
`

const Error = ({text}) => {
    return <ErrorStyled>{text}</ErrorStyled>
}

export default Error
